import React from 'react';

const Hero = () => {
  return (
    <section className="bg-gradient-to-br from-blue-600 to-indigo-700 text-white py-32 px-6 text-center">
      <h2 className="text-5xl font-extrabold mb-6">Intelligence Behind Every Financial Move</h2>
      <p className="text-xl mb-8 max-w-2xl mx-auto">Finlytiq LLP is your strategic partner for smart financial, tax, and compliance solutions.</p>
      <a href="#contact" className="bg-white text-blue-700 px-6 py-3 rounded-full font-semibold hover:bg-blue-100 transition duration-300">
        Get in Touch
      </a>
    </section>
  );
};

export default Hero;
