import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="bg-gradient-to-r from-purple-600 to-blue-500 text-white py-20 text-center">
      <motion.h1
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-4xl md:text-5xl font-bold"
      >
        Intelligence Behind Every Financial Move ðŸš€
      </motion.h1>
      <p className="mt-4 text-lg">Professional VCFO & startup growth solutions</p>
      <motion.button
        whileHover={{ scale: 1.1 }}
        className="mt-6 px-6 py-2 bg-white text-blue-600 font-semibold rounded shadow"
      >
        Book a Free Consultation
      </motion.button>
    </section>
  );
};

export default Hero;
